CREATE TABLE vmware_master_data (
    host_name text not null,
    host_id integer primary key not null,
    vmware_created integer default false, -- Has VM been created in VMware (1/true - 0/false) 
    rancher_allocated integer default false, -- Has VM been allocated to some Rancher cluster (1/true - 0/false)
    ip text null, -- VM IP Address
    sql_timestamp text GENERATED ALWAYS AS (datetime('now')) VIRTUAL,
    host_type text GENERATED ALWAYS AS (SUBSTR(host_name,6,1)) VIRTUAL -- calculated virtual column
  );

CREATE VIEW free_hostnames AS
SELECT *
FROM vmware_master_data as md
WHERE md.rancher_allocated IS False AND md.vmware_created IS False AND md.sql_timestamp < date('NOW', '-2 days')
ORDER BY md.sql_timestamp DESC; 


